import { useState } from "react";
import "./styles.css";

export default function ListingAd({ 
  pic, 
  title,
  address,
  description,
  project_type,
  year,
  ownership_type,
  psf_min,
  psf_max,
  subprice_label,
  availabilities_label,
  clickable_num1,
  descriptionMiddle,
  clickable_num2,
  descriptionMiddle3,
  clickable_num3,
  descriptionEnd
}) {
  const [showDescription, setShowDescription] = useState(false);
  const [realPhoneNumber, setRealPhoneNumber] = useState(false);

  const anonymizePhoneNumber = (phoneNumber) => {
    const visiblePart = phoneNumber.substring(0, 4);
    const hiddenPart = "XXXX";
    return `${visiblePart} ${hiddenPart}`;
  };

  const handlePhoneNumberClick = () => {
    setRealPhoneNumber(true);
  };

  const formattedDescription = description.split('\n').map((paragraph, index) => (
    <p key={index}>{paragraph}</p>
  ));

  return (
    <div className="App">
      <div className="image-container">
        {/* ... (unchanged) */}
      </div>
      <div className="mainContent">
        <div className="mainContent-header">
          <div className="mainContent-details">
            {/* ... (unchanged) */}
            <div className="property-type-container">
              <p className="property-type">{project_type} · {year} · {ownership_type}</p>
              <p className="availabilities-label">{availabilities_label}</p>
            </div>
          </div>
          <div className="property-price">
            <p className="price-psf"> ${psf_min} - ${psf_max} psf</p>
            <p className="price-sublabel">{subprice_label}</p>
          </div>
        </div>
        <div>
          <button onClick={() => setShowDescription(!showDescription)}>
            {showDescription ? "Hide Description" : "Show Description"}
          </button>
          {showDescription && (
            <div>
              <div onClick={handlePhoneNumberClick}>
                <p>{realPhoneNumber ? clickable_num1 : anonymizePhoneNumber(clickable_num1)}</p>
              </div>
              {formattedDescription}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
